import React, {Component} from 'react';
import './Form.css';

class Form extends Component {
    constructor(props) {
    super(props);
    this.state = {
        name: '',
        ID: ''
    };
    this.getName = this.getName.bind(this);
    this.getID = this.getName.bind(this);

    }

    getName(event) {
        this.setState({value: event.target.value})
    }

    getID(event){
        this.setState({value: event.target.value})
    }

    render() {
        return(
            <div>
                <form className="form">
                    <label>Name:</label>
                    <input type="text" value="{this.state.name}" onChange="{this.getName}" />
                    <label>ID:</label>
                    <input type="text" value="{this.state.value}" onChange="{this.getId}" />
                    <input type="submit" value="Submit"></input>
                </form>
            </div>

        );
    }

}
export default Form;
